mapper.select:
select count(car_maintenance_id) from car_maintenance as cm left join car on car.car_id = cm.car_id where 1=1
and car.car_number like "%"#{carNumber}"%"
and cm.`state` = #{state}

mapper.update:
update car_maintenance
set
`state` =#{carMaintenance.state,jdbcType=INTEGER},
address =#{carMaintenance.address,jdbcType=VARCHAR},
content = #{carMaintenance.content,jdbcType=VARCHAR}
where car_maintenance_id = #{carMaintenance.carMaintenanceId,jdbcType=INTEGER}
