mapper.select:

select finance_money_id, amount, finance_money_type, create_time, `comment`, serial_number,
finance_object,unit
from finance_money_flow


        AND create_time >= #{createTime}


        AND create_time <= #{endTime}


        AND finance_money_type like "%"#{financeMoneyType}"%"


        AND unit = #{unit}


ORDER BY finance_money_id DESC


select count(finance_money_id) AS count
from finance_money_flow


        AND create_time >= #{createTime}


        AND create_time <= #{endTime}


        AND finance_money_type like "%"#{financeMoneyType}"%"


        AND unit = #{unit}


ORDER BY finance_money_id DESC
