mapper.select:
SELECT gds.gd_id,gds.goods_id,gd.delivery_number,gd.good_type,gds.app_user_id,gds.good_state,gds.payment_status,
gds.add_time,gds.update_time,gd.transport_type,gds.category
FROM goods_details AS gds
LEFT JOIN goods AS gd ON (gd.goods_id =gds.goods_id)

    gds.app_user_id = #{appUserId} AND gd.transport_type <> #{transportType1}

        AND gd.delivery_number like "%"#{deliveryNumber}"%"


        AND gds.good_state = #{goodState}


        AND gd.transport_type = #{transportType}

    OR
    gds.app_user_id = #{appUserId} AND gd.transport_type <> #{transportType1}

        AND gd.delivery_number like "%"#{deliveryNumber}"%"


        AND gds.payment_status = #{goodState}


        AND gd.transport_type = #{transportType}


ORDER BY gds.gd_id DESC

SELECT count(gds.gd_id)AS count
FROM goods_details AS gds
LEFT JOIN goods AS gd ON (gd.goods_id =gds.goods_id)

    gds.app_user_id = #{appUserId} AND gd.transport_type <> #{transportType1}

        AND gd.delivery_number like "%"#{deliveryNumber}"%"


        AND gds.good_state = #{goodState}


        AND gd.transport_type = #{transportType}

    OR
    gds.app_user_id = #{appUserId} AND gd.transport_type <> #{transportType1}

        AND gd.delivery_number like "%"#{deliveryNumber}"%"


        AND gds.payment_status = #{goodState}


        AND gd.transport_type = #{transportType}


ORDER BY gds.gd_id DESC

mapper.update:
update goods_details
set good_state =#{goodState,jdbcType=VARCHAR},
update_time =#{updateTime,jdbcType=TIMESTAMP}
where goods_id =#{goodsId,jdbcType=INTEGER}

update goods_details
set good_state =#{goodState,jdbcType=VARCHAR},
update_time =#{updateTime,jdbcType=TIMESTAMP}
where 1=1

    and goods_id =#{goodsId,jdbcType=INTEGER}

mapper.select:
select gds.pay_way as payWay,gds.payment_status as paymentStatus ,gd.delivery_number
from goods_details as gds
left join goods gd on gds.goods_id = gd.goods_id
where 1=1

    and gds.app_user_id = #{appUserId}


    and gd.delivery_number = #{deliveryNumber}

mapper.update:
update goods_details
set payment_status = #{details.paymentStatus,jdbcType=VARCHAR},

    pay_way =#{details.payWay,jdbcType=VARCHAR},


    good_state =#{details.goodState,jdbcType=VARCHAR},

update_time =#{details.updateTime,jdbcType=VARCHAR}
where goods_id =#{details.goodsId,jdbcType=INTEGER}

mapper.select:
SELECT gds.gd_id,gds.goods_id,gd.delivery_number,gd.create_time,wp.wp_number AS
into_wp,gds.singapore_area_id,gds.rally_point_id,gd.exit_way
FROM goods AS gd
LEFT JOIN goods_details AS gds ON (gd.goods_id = gds.goods_id)
LEFT JOIN warehouse_positions AS wp ON (gd.into_warehouse_positions_id = wp.wp_id)

    gds.good_state = #{goodState} AND gds.warning_state = #{warningState}

        AND gd.create_time like "%"#{createTime}"%"


        AND gds.singapore_area_id = #{singaporeAreaId}


        AND gds.rally_point_id = #{rallyPointId}


        AND gd.exit_way like "%"#{exitWay}"%"

mapper.update:

update goods_details
set
item_value = #{itemValue,jdbcType=VARCHAR}
where gd_id = #{gdId,jdbcType=INTEGER}


update goods_details
set
is_receive_goods = 1,
send_time = #{date,jdbcType=TIMESTAMP}
where goods_id = #{goodsId,jdbcType=INTEGER}

mapper.select:
SELECT gd.delivery_number,gd.good_type,gd.zip_code,gds.receipt_address, gds.receipt_contact,gds.receipt_contact_mobile,bag.bag_number,gd.total_goods,gds.actual_weight,gds.item_value, gds.is_arrival_pay,gds.gst_price FROM goods_details AS gds LEFT JOIN goods AS gd ON (gd.goods_id =gds.goods_id) LEFT JOIN bag ON (gd.bag_id = bag.bag_id) where gd.goods_id in
#{item}
ORDER BY gd.zip_code ASC

SELECT gd.delivery_number,gd.tripartite_number,bag.bag_number FROM goods_details AS gds LEFT JOIN goods AS gd ON (gd.goods_id =gds.goods_id) LEFT JOIN bag ON (gd.bag_id = bag.bag_id) where gd.goods_id in
#{item}
ORDER BY bag.bag_id ASC
