mapper.select:

select message_id, app_user_id, title, content, `type`,category, `state`, add_time, update_time
from message WHERE app_user_id = #{appUserId} AND category = #{category}
ORDER BY add_time DESC


select count(message_id) AS count
from message WHERE app_user_id = #{appUserId} AND category = #{category}
ORDER BY add_time DESC

mapper.update:

update message
set `state` = #{state,jdbcType=VARCHAR},
update_time = #{updateTime,jdbcType=TIMESTAMP}
where message_id = #{messageId,jdbcType=INTEGER}

mapper.select:

select message_id, app_user_id,en_title AS title,en_content AS content,en_type AS `type`, `state`, add_time,
update_time
from message WHERE message_id = #{messageId}


select message_id, app_user_id, title, content, `type`, `state`, add_time,
update_time,en_title,en_content,en_type,comment
from message where comment = #{comment}
