mapper.select:

select singapore_area_id, singapore_point_id, lat, lng, del_state, create_time
from singapore_point
where singapore_point_id = #{singaporePointId,jdbcType=INTEGER}

select lat,lng,singapore_area_id from singapore_point
where  singapore_area_id =#{singaporeAreaId,jdbcType=INTEGER}
order by singapore_point_id
